import React from 'react';
import { motion } from 'framer-motion';

const ConferenceCard = ({ title, themeColor = '#2563eb' }) => {
    return (
        <motion.article
            className="conference-card"
            whileHover={{ y: -8, boxShadow: `0 20px 40px -10px ${themeColor}30` }}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
            style={{
                background: `linear-gradient(135deg, #ffffff 0%, ${themeColor}08 100%)`,
                border: `1px solid ${themeColor}30`,
                borderLeft: `4px solid ${themeColor}`
            }}
        >
            <div className="conference-card-header">
                <span
                    className="conference-status"
                    style={{
                        color: themeColor,
                        backgroundColor: `${themeColor}15`,
                        border: `1px solid ${themeColor}20`
                    }}
                >
                    Upcoming
                </span>
                <span className="conference-date">2026</span>
            </div>

            <h3 className="conference-title">{title}</h3>

            <div className="conference-actions">
                <button
                    className="btn-register"
                    aria-label={`Register for ${title}`}
                    style={{ backgroundColor: themeColor, boxShadow: `0 4px 14px 0 ${themeColor}60` }}
                >
                    View / Register
                </button>
            </div>
        </motion.article>
    );
};

export default ConferenceCard;
