import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const CategoryCard = ({ category }) => {
    const { id, title, events } = category;

    return (
        <Link to={`/conferences/${id}`} className="category-card-link">
            <motion.div
                className="category-block"
                whileHover={{ y: -5, boxShadow: "0 10px 25px -5px rgba(0, 0, 0, 0.1), 0 8px 10px -6px rgba(0, 0, 0, 0.1)" }}
                whileTap={{ scale: 0.98 }}
            >
                <div className="card-decoration"></div>
                <h3 className="category-block-title">{title}</h3>
                <span className="category-count">{events.length} Events</span>
                <div className="category-arrow">View All &rarr;</div>
            </motion.div>
        </Link>
    );
};

export default CategoryCard;
