import React, { useMemo } from 'react';
import { useParams, Link, Navigate } from 'react-router-dom';
import { Helmet } from 'react-helmet-async';
import { conferencesData } from '../data/conferences';
import ConferenceList from '../components/ConferenceList';

const CategoryDetail = () => {
    const { slug } = useParams();

    const category = useMemo(() =>
        conferencesData.find(c => c.id === slug),
        [slug]
    );

    if (!category) {
        return <Navigate to="/" replace />;
    }

    const { title, events } = category;
    const count = events.length;

    const colorMap = {
        'business-economics': '#0ea5e9',
        'health-medical-sciences': '#ef4444',
        'engineering-technology': '#f59e0b',
        'education': '#3b82f6',
        'physical-life-sciences': '#10b981',
        'social-science-humanities': '#8b5cf6',
        'agriculture-food-sciences': '#84cc16',
        'interdisciplinary-emerging-fields': '#ec4899',
        'mathematics-data-science': '#6366f1',
        'arts-culture-communication': '#f97316',
        'sports-physical-education': '#14b8a6',
    };

    const themeColor = colorMap[slug] || '#2563eb';

    return (
        <>
            <Helmet>
                <title>{`${title} Conferences (${count})`}</title>
                <meta
                    name="description"
                    content={`Browse ${count} upcoming conferences in ${title}. Register now.`}
                />
                <link rel="canonical" href={`/conferences/${slug}`} />
            </Helmet>

            <main className="container" style={{ paddingTop: '3rem' }}>
                <Link
                    to="/"
                    className="back-link"
                    style={{
                        display: 'inline-flex',
                        alignItems: 'center',
                        gap: '8px',
                        background: `linear-gradient(135deg, ${themeColor}, ${themeColor}dd)`,
                        color: 'white',
                        padding: '0.75rem 1.5rem',
                        borderRadius: '12px',
                        fontWeight: '600',
                        fontSize: '0.95rem',
                        textDecoration: 'none',
                        boxShadow: `0 4px 15px ${themeColor}30`,
                        border: 'none',
                        transition: 'all 0.2s ease',
                        marginBottom: '2.5rem'
                    }}
                    onMouseEnter={(e) => {
                        e.currentTarget.style.boxShadow = `0 6px 25px ${themeColor}40`;
                        e.currentTarget.style.transform = 'translateY(-2px)';
                    }}
                    onMouseLeave={(e) => {
                        e.currentTarget.style.boxShadow = `0 4px 15px ${themeColor}30`;
                        e.currentTarget.style.transform = 'translateY(0)';
                    }}
                >
                    <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round">
                        <path d="M19 12H5M12 19l-7-7 7-7" />
                    </svg>
                    Back to all categories
                </Link>
                <header style={{
                    marginBottom: '3rem',
                    paddingBottom: '2rem',
                    borderBottom: `3px solid ${themeColor}20`
                }}>
                    <h1 style={{
                        fontSize: '3rem',
                        fontWeight: '800',
                        background: `linear-gradient(135deg, ${themeColor} 0%, ${themeColor}dd 100%)`,
                        WebkitBackgroundClip: 'text',
                        WebkitTextFillColor: 'transparent',
                        backgroundClip: 'text',
                        marginBottom: '0.75rem',
                        letterSpacing: '-0.02em'
                    }}>
                        {title}
                    </h1>
                    <p className="subtitle" style={{
                        fontSize: '1.1rem',
                        color: '#64748b',
                        fontWeight: '500',
                        display: 'inline-flex',
                        alignItems: 'center',
                        gap: '0.5rem',
                        background: `${themeColor}10`,
                        padding: '0.5rem 1rem',
                        borderRadius: '99px',
                        border: `1px solid ${themeColor}20`
                    }}>
                        <span style={{
                            width: '8px',
                            height: '8px',
                            background: themeColor,
                            borderRadius: '50%',
                            display: 'inline-block'
                        }}></span>
                        {count} events available
                    </p>
                </header>

                <section>
                    <ConferenceList events={events} themeColor={themeColor} />
                </section>
            </main>
        </>
    );
};

export default CategoryDetail;
