import React, { useState } from 'react';
import { Helmet } from 'react-helmet-async';
import { conferencesData } from '../data/conferences';
import CategoryAccordion from '../components/CategoryAccordion';

const Home = () => {
    // State to track the currently expanded category ID
    const [expandedId, setExpandedId] = useState(null);

    const handleToggle = (id) => {
        setExpandedId(prevId => (prevId === id ? null : id));
    };

    return (
        <>
            <Helmet>
                <title>Conference Events Listing</title>
                <meta name="description" content="Browse upcoming international conferences across various disciplines." />
                <link rel="canonical" href="/" />
            </Helmet>

            <main className="container">
                <header>
                    <h1>Conference Categories</h1>
                    <p className="subtitle">Select a category to view upcoming events</p>
                </header>

                <section aria-label="Conference Categories">
                    {conferencesData.map((category) => (
                        <CategoryAccordion
                            key={category.id}
                            category={category}
                            isExpanded={expandedId === category.id}
                            onToggle={() => handleToggle(category.id)}
                        />
                    ))}
                </section>
            </main>
        </>
    );
};

export default Home;
