import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import {
    FaGraduationCap, FaDna, FaLandmark, FaLeaf,
    FaAtom, FaChartLine, FaPalette, FaRunning,
    FaMicrochip, FaHeartbeat, FaGlobeAmericas
} from 'react-icons/fa';

const iconMap = {
    'education': <FaGraduationCap />,
    'physical-life-sciences': <FaDna />,
    'social-science-humanities': <FaLandmark />,
    'agriculture-food-sciences': <FaLeaf />,
    'interdisciplinary-emerging-fields': <FaAtom />,
    'mathematics-data-science': <FaChartLine />,
    'arts-culture-communication': <FaPalette />,
    'sports-physical-education': <FaRunning />,
    'engineering-technology': <FaMicrochip />,
    'health-medical-sciences': <FaHeartbeat />,
    'business-economics': <FaGlobeAmericas />
};

const colorMap = {
    'business-economics': '#0ea5e9', // Sky Blue
    'health-medical-sciences': '#ef4444', // Red
    'engineering-technology': '#f59e0b', // Amber
    'education': '#3b82f6', // Royal Blue
    'physical-life-sciences': '#10b981', // Emerald
    'social-science-humanities': '#8b5cf6', // Violet
    'agriculture-food-sciences': '#84cc16', // Lime
    'interdisciplinary-emerging-fields': '#ec4899', // Pink
    'mathematics-data-science': '#6366f1', // Indigo
    'arts-culture-communication': '#f97316', // Orange
    'sports-physical-education': '#14b8a6', // Teal
};

const CategoryCard = ({ id, title, events = [] }) => {
    const icon = iconMap[id] || <FaAtom />;
    const themeColor = colorMap[id] || '#2563eb';

    return (
        <Link to={`/conferences/${id}`} className="category-card-link">
            <motion.div
                className="category-block"
                whileHover={{ y: -5 }}
                whileTap={{ scale: 0.98 }}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.4 }}
                style={{
                    border: '2px solid transparent',
                    backgroundImage: `linear-gradient(135deg, #ffffff, #f8faff), linear-gradient(135deg, ${themeColor}40, ${themeColor})`,
                    backgroundOrigin: 'border-box',
                    backgroundClip: 'padding-box, border-box',
                    boxShadow: `0 10px 30px -5px ${themeColor}20` // Added colored shadow
                }}
            >
                <div className="category-content-wrapper">
                    <div
                        className="category-icon-wrapper"
                        style={{ color: themeColor, backgroundColor: `${themeColor}15`, borderColor: `${themeColor}30` }}
                    >
                        {icon}
                    </div>
                    <div>
                        <h3 className="category-block-title" style={{ color: '#0f172a' }}>{title}</h3>
                    </div>

                    <div className="category-meta">
                        <span
                            className="category-count"
                            style={{ backgroundColor: `${themeColor}10`, color: themeColor }}
                        >
                            {events.length} Events
                        </span>
                        <div className="category-action" style={{ color: themeColor }}>
                            <span>Explore</span>
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round">
                                <path d="M5 12h14M12 5l7 7-7 7" />
                            </svg>
                        </div>
                    </div>
                </div>
            </motion.div>
        </Link>
    );
};

export default CategoryCard;
